type Flags = [boolean, boolean, boolean, boolean, boolean, boolean, boolean, boolean];
/** The Reader helps reading serial data from a binary buffer,
 * advancing a cursor as it does.
 */
export default class Reader {
    buffer: Uint8Array;
    cursor: number;
    constructor(buffer: Uint8Array, cursor?: number);
    readBytes(size: number): Uint8Array;
    readBool(): boolean;
    readFlags(): Flags;
    readByte(): number;
    readUInt32(): number;
    readNumber(): number;
    readBigint(): bigint;
    advance(count: number): number;
    tell(): number;
    seek(pos: number): this;
}
export {};
